class ShortElements{constructor(a){this.items=a}isSingle(){return 1===this.items.length}first(){return this.items[0]}value(a){if(a){if(this.isSingle())return this.first().value=a;this.forEach(b=>b.value=a)}else if(this.isSingle())return this.first().value;else{let a=[];return this.forEach(b=>a.push(b.value)),a}}forEach(a){this.items.forEach(a)}event(a,b){Array.isArray(a)?a.forEach(a=>{this.isSingle()?this.first().addEventListener(a,b):this.forEach(c=>c.addEventListener(a,b))}):this.isSingle()?this.first().addEventListener(a,b):this.forEach(c=>c.addEventListener(a,b))}}function s(a){return new ShortElements(document.querySelectorAll(a))}